import React, { Component } from "react";
import { default as ReactSelect } from "react-select";
import { components } from "react-select";
import {useState} from 'react'

const Option = (props) => {
    const [form, setForm] = useState({
                id: "",
                name: "",
                brand: "",
                price: "",
                chemicals: [""]
              })



            // Displays new contact form and calls necessary functions, links back to home.



  return (

    <div>

      <components.Option {...props}>
        <input
          type="checkbox"
          checked={props.isSelected}
          onChange={() => null}
        />{" "}
        <label>{props.label}</label>
      </components.Option>
    </div>
  );
};


const colourOptions = [
    { value: "ocean1", label: "Ocean" },
    { value: "blue", label: "Blue" },
    { value: "purple", label: "Purple" },
    { value: "red", label: "Red" },
    { value: "orange", label: "Orange" },
    { value: "yellow", label: "Yellow" },
    { value: "green", label: "Green" },
    { value: "forest", label: "Forest" },
    { value: "slate", label: "Slate" },
    { value: "silver", label: "Silver" }
  ];

export default class Example extends Component {
  constructor(props) {
    super(props);
    this.state = {
      optionSelected: null
    };
  }

  handleChange = (selected) => {
    this.setState({
      optionSelected: selected
    });
  };



  render() {
    return (
      <span
        className="d-inline-block"
        data-toggle="popover"
        data-trigger="focus"
        data-content="Please selecet account(s)"
      >
        <ReactSelect
          options={colourOptions}
          isMulti
          closeMenuOnSelect={false}
          hideSelectedOptions={false}
          components={{
            Option
          }}
          onChange={this.handleChange}
          allowSelectAll={true}
          value={this.state.optionSelected}
        />
        <div className="box" style={{marginLeft: "646px", marginRight:"790px", width:"18%",  marginTop: "-40px", alignItems:'center'}}>
            <form onSubmit={handleSubmit}>
              <div>
                <input onChange={handleChange} value={form.name} 
                    type="text" name="name" placeholder="Name" />
                <input onChange={handleChange} value={form.description} 
                    type="text" name="description" placeholder="Description" />
                <input onChange={handleChange} value={form.number} 
                    type="tel" maxlength="10" name="number" placeholder="Number" />
              </div>
              <button type="submit">
                Add Contact
              </button>
            </form>
            </div>

      </span>
    );
  }
}





















